mapper.select:
SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT b.boardIdx, b.memberIdx, m1.userId, m2.lastName || m2.firstName userName, categoryIdx, subject, TO_CHAR(b.createDate, 'YYYY-MM-DD') createDate, hitCount FROM board b JOIN member m1 ON b.memberIdx = m1.memberIdx JOIN memberInfo m2 ON b.memberIdx = m2.memberIdx LEFT OUTER JOIN ( SELECT boardIdx, COUNT(*) replyCount FROM boardReply GROUP BY boardIdx ) c ON b.boardIdx = c.boardIdx
categoryIdx = 1
ORDER BY boardIdx DESC
) tb WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}
